import { Component } from "react";
import Aux from '../../hoc/Aux';
import Burger from '../../components/Burger/Burger';
import BuildControls from '../../components/Burger/BulidControls/BuildControls'
import Modal from '../../components/UI/Modal/Modal'
import OrderSummary from '../../components/Burger/OrderSummary/OrderSummary'
import axios from '../../axios-orders'
import Spinner from "../../components/UI/Spinner/Spinner";
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler'
import {useNavigate,useLocation} from 'react-router-dom';
import Checkout from '../Checkout/Checkout'
import {Route} from 'react-router-dom'


const INGREDIENT_PRICES = {
    salad : 0.5,
    cheese : 0.4,
    meat : 1.3,
    bacon : 0.7
};

class BurgerBuilder extends Component{



    state = {
        ingredients:null,

      //   ingredients:{
      //     salad:0,
      //     bacon:0,
      //     cheese:0,
      //     meat:0
      // },

        totalPrice: 4,

        purchasing: false,

        purchasable:false,
        loading: false,
    }

    componentDidUpdate(){

      console.log("componenet did update")

    }

    componentDidMount(){
      console.log("printing props");
      console.log(this.props)

      console.log("componenet did mount")

      this.setState({loading:true});

      axios.get('/ingredients.json').then(response=>{
        this.setState({loading:false});

        console.log(response);

        this.setState({ingredients:response.data});

      }).catch(error=>{
        this.setState({loading:false});


        console.log(error)

      })

     }

     postIngredientHandler=()=>{

       const postIngredientReq = {
        salad:2,
        bacon:0,
        cheese:0,
        meat:0

       }

       axios.post('/ingredients.json',postIngredientReq)
       .then(response=>{
         console.log(response);
       }).catch(error=>{
         console.log(error);
       });
     }

    render () {

        const disabledInfo={
            ...this.state.ingredients
        }

        for (let key in disabledInfo){
            disabledInfo[key] = disabledInfo[key] <=0
        }

        let burger = null
        let orderSummary=null;
       let checkout = null ;

        if(this.state.ingredients==null){
          burger = <Spinner /> 
          orderSummary = <Spinner /> 

        }else{
          burger = (
            <Aux>
            <Burger ingredients={this.state.ingredients} />
            <BuildControls 
                ingredientAdded = {this.addIngredientHandler} 
                ingredientRemoved={this.removeIngredientHandler} 
                disabled={disabledInfo} price={this.state.totalPrice}
                purchasable={this.state.purchasable}
                ordered = {this.purchaseHandler}
             />
             </Aux>
          );

          orderSummary = (

            <OrderSummary ingredients={this.state.ingredients}
               purchaseCancelled={this.purchaseCancelHandler} 
                purchaseContinued={this.purchaseContinueHandler} 
                price={this.state.totalPrice}
                >
            </OrderSummary>

          );

          checkout = (
            <Checkout/>
          );



          if(this.state.loading ){
            burger = <Spinner /> 
            orderSummary = <Spinner />
          }
        }

        return (

            <Aux>
            <Modal show={this.state.purchasing}  modalClosed={this.purchaseCancelHandler}>
                {orderSummary}
                  </Modal>
                {burger}

                <Route path="/" exact element={burger} />
                <Route path="/checkout" exact element={checkout} />


            </Aux>
        );
    }


    addIngredientHandler=(type) =>{
        const oldCount=this.state.ingredients[type];
        const updatedCount=oldCount + 1;
        const updatedIngredients={
            ...this.state.ingredients
        }
        updatedIngredients[type]=updatedCount;
        const priceAddition=INGREDIENT_PRICES[type];
        const oldPrice = this.state.totalPrice;
        const newPrice = oldPrice + priceAddition;

        this.setState({totalPrice:newPrice, ingredients:updatedIngredients})
        this.updatePurchaseState(updatedIngredients)
    }

    removeIngredientHandler=(type) =>{
        const oldCount=this .state.ingredients[type];
        const updatedCount=oldCount - 1;
        const updatedIngredients={
            ...this.state.ingredients
        }
        updatedIngredients[type]=updatedCount;
        const priceAddition=INGREDIENT_PRICES[type];
        const oldPrice = this.state.totalPrice;
        const newPrice = oldPrice - priceAddition;

        this.setState({totalPrice:newPrice, ingredients:updatedIngredients})
        this.updatePurchaseState(updatedIngredients)
    }

    //.......................................


    updatePurchaseState = (ingredients) => {
      console.log(ingredients);
        const sum = Object.keys(ingredients)
          .map(igKey => {
            return ingredients[igKey];
          })
          .reduce((sum, el) => {
            return sum + el;
          }, 0);
          return this.setState({ purchasable: (sum > 0)});
      }

      purchaseHandler = () => {
        this.setState({
            purchasing: true
          });
        // if(this.props.isAuthenticated){
        //   this.setState({
        //     purchasing: true
        //   });
        // } else {
        //   this.props.onSetAuthRedirectPath("/checkout");
        //   this.props.history.push("/auth");
        // }
      }

      purchaseCancelHandler = () => {
        this.setState({
          purchasing: false
        })
      }

      purchaseContinueHandler = () => {
        this.props.navigate("/checkout")
       // this.setState({loading:true})
      //  const postOrderRequest={
      //       ingredients : this.state.ingredients,
      //       price: this.state.price,
      //       customerName:{
      //         name:'David Oteng',
      //         address:{
      //           street: 'East Legon',
      //           zipcode: '233',
      //           country: 'Ghana'
      //       },
      //       email:'daveotengo@gmail.com',

      //       },
      //       deliverymethod:'fastest'
      //   }
      //   axios.post('/orders.json',postOrderRequest)
      //   .then(response=>{

      //     this.setState({loading:false,purchasing:false});

      //     console.log(response);
      //   }).catch(error=>{
      //     this.setState({loading:false,purchasing:false});

      //     console.log("printing error");
      //     console.log(error);
      //   });



        //alert("you can continue");
       // this.props.onInitPurchase();
        //this.props.history.push('/checkout');
      }  
}

//export default withErrorHandler(BurgerBuilder,axios);

function WithNavigate(props) {
  let navigate = useNavigate();
  const location = useLocation();

  //return <Blog {...props} navigate={navigate} location={location} />
  return <BurgerBuilder {...props} navigate={navigate} location={location} />
}

export default  WithNavigate;
