import { Component }  from 'react';

import { Router,Routes,Route } from 'react-router-dom';
import { connect } from 'react-redux';
import CheckoutSummary from '../../components/Order/CheckoutSummary/CheckoutSummary';
import ContactData from './ContactData/ContactData';
import {useNavigate,useLocation} from 'react-router-dom';
import Aux from '../../hoc/Aux'

class Checkout extends Component {

  componentDidMount(){

    console.log("in checkout")
    console.log("component did mount");
    console.log(this.props.ings);
    console.log(this.props.location);
    console.log(this.props.navigate);



  }

  checkoutCancelledHandler = () => {
    this.props.navigate(-1);
  }

  checkoutContinuedHandler = () => {
    this.props.navigate('/contact-data');
  }



  render () {

    let checkoutSummary = null;

    checkoutSummary = (<CheckoutSummary
      ingredients={this.props.ings}
      checkoutCancelled={this.checkoutCancelledHandler}
      checkoutContinued={this.checkoutContinuedHandler}

      />)
    //let summary = <Route to="/" />
    let summary =null
   // if (this.props.ings) {
      //let purchasedRedirect = this.props.purchased ? <Route to="/" /> : null;

    //}
    return (

      <Aux>
      {/* {purchasedRedirect} */}
      {checkoutSummary}
      {/* {<ContactData />} */}
      <Router>
      <Routes>    
                <Route path="/contact-data" exact element={<ContactData />} />

            </Routes>
            </Router>


</Aux>

    );
  }
}

const mapStateToProps = state => {
  return {
    ings: state.burgerBuilder.ingredients,
    purchased: state.orders.purchased
  }
};


//export default connect(mapStateToProps, null)(Checkout);
//export default Checkout;


function WithNavigate(props) {
  let navigate = useNavigate();
  const location = useLocation();

  return <Checkout {...props} navigate={navigate} location={location} />
}

export default  WithNavigate;
